mapper.update:

UPDATE TBL_USER
SET USERPW = #{userpw}, USERNAME = #{username}
WHERE USERID = #{userid}


UPDATE TBL_USER
SET USERPW = #{userpw}, USERNAME = #{username}, PROFILEPATH = #{profilepath}
WHERE USERID = #{userid}


UPDATE TBL_USER
SET ROLE = #{role}
WHERE USERID = #{userid}


UPDATE TBL_USER
SET WITHDRAWAL = 1
WHERE USERID = #{userid}


UPDATE TBL_USER
SET USERPW = #{userpw}
WHERE USERID = #{userid}

mapper.select:

SELECT COUNT(*) 
FROM TBL_USER 
WHERE USERID = #{userid}


SELECT COUNT(*) 
FROM TBL_USER 
WHERE LOWER(USERNAME) = LOWER(#{username}) AND USERID != #{userid}

mapper.update:
    UPDATE TBL_USER

	    SET WITHDRAWAL = 1, DEACTIVEDATE = #{deactiveDate}


