mapper.update:

UPDATE TBL_USER
SET USERPW = #{userpw}, USERNAME = #{username}, PROFILEPATH = #{profilepath}
WHERE USERID = #{userid}


UPDATE TBL_USER
SET ROLE = #{role}
WHERE USERID = #{userid}


UPDATE TBL_USER
SET USERPW = #{userpw}
WHERE USERID = #{userid}

mapper.select:

SELECT COUNT(*) 
FROM TBL_USER 
WHERE USERID = #{userid}


SELECT COUNT(*) 
FROM TBL_USER 
WHERE USERNAME = #{username} AND USERID != #{userid}
